			if ($remarketing_query->num_rows) { 
				$allowed_utm = ['first_referrer', 'last_referrer', 'utm_source', 'utm_medium', 'utm_content', 'utm_term', 'utm_campaign', 'utm_referrer'];
				$data['utm_remarketing_data'] = array_filter(array_intersect_key($remarketing_query->rows[0], array_flip($allowed_utm)), function($value) {
					return !empty($value);
				});

			$allowed_time = ['success_page', 'ga4', 'facebook', 'facebook_lead', 'esputnik', 'telegram', 'tiktok'];
			$data['time_remarketing_data'] = array_filter(array_intersect_key($remarketing_query->rows[0], array_flip($allowed_time)), function($value) {
				return $value !== '0000-00-00 00:00:00';
			});
			}
		}

